const React = require("react");

const createMock = () => {
    const View = props => React.createElement("view", props);
    const Text = props => React.createElement("text", props);

    const StyleSheet = {
        create: object => object,
    };

    const Platform = {
        select: (obj) => {
            return obj[Object.keys(obj)[0]];
        },
    };

    return {
        View,
        Text,
        StyleSheet,
        Platform,
    };
};

module.exports = createMock;
